import '../App.css';
import React from 'react';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import axios from 'axios';

class OpenSYR extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      diameter :"",
      length :"",
      volume:""
    };
  }



    render(){
      return  <body>
                <Form action="/api/init_python" method="post" onSubmit={this.init_python()}>
                  <Form.Group>
                        <Form.Row>
                          <Form.Label column="lg" lg={2}>
                            Syringe diameter
                          </Form.Label>
                              <Form.Control type="text" placeholder="Syringe diameter (cm)" value={this.state.diameter} />
                        </Form.Row>
                        <br />
                        <Form.Row>
                          <Form.Label column="lg" lg={8}>
                            Length of the experience
                          </Form.Label>
                            <Form.Control type="text" placeholder="Length of the experience (s)" value={this.state.length}/>
                        </Form.Row>
                        <br />
                        <Form.Row>
                          <Form.Label column="lg" lg={2}>
                            Volume to inject
                          </Form.Label>
                            <Form.Control type="text" placeholder="Volume to inject (mL)" value={this.state.volume}/>
                        </Form.Row>
                    </Form.Group>
                  <Button variant="primary" type="submit">Valider les donn√©es</Button>                  
                </Form>
              </body>
    }

    init_python() {
        axios.post("/api/init_python", this.state)

    }
  }

  export default OpenSYR
;
