import '../App.css';
import React from 'react';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import axios from 'axios';

class OpenSYR extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      diameter :"",
      length :"",
      volume:""
    };
  }



    render(){
      return  <body>
                <Form action="/api/init_python" method="post" onSubmit={this.init_python}>
                  <Form.Group>
                        <Form.Row>
                          <Form.Label column="lg" lg={2}>
                            Syringe diameter
                          </Form.Label>
                              <Form.Control type="text" placeholder="Syringe diameter (cm)" value={this.state.diameter}
                              onChange={e => this.setState({ diameter: e.target.value })} />
                        </Form.Row>
                        <br />
                        <Form.Row>
                          <Form.Label column="lg" lg={8}>
                            Length of the experience
                          </Form.Label>
                            <Form.Control type="text" placeholder="Length of the experience (s)" value={this.state.length}
                            onChange={e => this.setState({ length: e.target.value })}/>
                        </Form.Row>
                        <br />
                        <Form.Row>
                          <Form.Label column="lg" lg={2}>
                            Volume to inject
                          </Form.Label>
                            <Form.Control type="text" placeholder="Volume to inject (mL)" value={this.state.volume}
                            onChange={e => this.setState({ volume: e.target.value })}/>
                        </Form.Row>
                    </Form.Group>
                  <Button variant="primary" type="submit">Valider les donn√©es</Button>                  
                </Form>
              </body>
    }






    init_python(e) {
        e.preventDefault();
        const formData = new FormData();

        //Using this config in my request, the response gives me the mentioned waring of missing boundary
        const config = {
            headers:{
                'Content-Type' : 'multipart/form-data',
            }
        };
        state = this.state[0]
        formData.append(state);

        axios.post("/api/init_python", formData, config)

    }










  }

  export default OpenSYR
;
