import '../App.css'
import React from 'react'
import {Container} from 'reactstrap'
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav'

class Home extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      currentTime: 0
    };
  }

  componentDidMount() {
    fetch('/time').then(res => res.json()).then(data => {
      this.setState({currentTime: data.time});
    });
  }

    render(){
      const fond = {
        color: "white",
        backgroundColor: "DodgerBlue",
        padding: "10px",
        fontFamily: "Arial"
      };
      return (

        <Container style={fond}>

          <style>
            background-image:url('../fond.jpeg');
            background-attachment:fixed;
          </style>
          <p>
            Welcome on the OpenSYR project
          </p>

          <p>The current time is <code>{this.state.currentTime}</code>.</p>
        </Container>
      );
    }
  }

  export default Home
;
