import '../App.css';
import React from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import axios from 'axios';

class Running extends React.Component{
  constructor(props) {
    super(props);          
    this.state={uploadProgress : 0 }
    this.HandleSubmit = this.HandleSubmit.bind(this);  
  }

  componentDidMount() {      
      this.interval = setInterval(() => this.setState({ uploadProgress: this.state.uploadProgress+1 }), 1000);          
  }

  HandleSubmit(e){
    e.preventDefault();
    //console.log("SUBMIT")
    const formData = new FormData();
    //Using this config in my request, the response gives me the mentioned waring of missing boundary
    const config = {
        headers:{
            'Content-Type' : 'application/json',
        }
    };
    //formData.append("file", this.state.to_upload);
    axios.post("/api/stop_experience", this.state, config)
    .then(response => {
        console.log(response)
    })
  }


    render(){
      return <div className="App-main" >

                <br></br>                 
                <h1>The experience is running</h1><br></br><br></br><br></br>

                <ProgressBar variant="warning" animated now={this.state.uploadProgress} label={`${this.state.uploadProgress}% completed`} />
                <Form action="./api/stop_experience" method="post" onSubmit={this.HandleSubmit} >
                  <Button variant="danger" type="submit" disabled={this.state.uploadProgress > 100}>STOP EXPERIENCE</Button><br></br> 
                </Form>
                <Form action="./api/stop_experience" method="post" onSubmit={this.HandleSubmit} >
                  <Button variant="primary" type="submit" disabled={this.state.uploadProgress < 100}>End experience</Button>
                </Form>

            </div>
      }


}   
export default Running;
