import '../App.css';
import React from 'react';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import axios from 'axios';

class OpenSYR extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      currentTime: 0
    };
  }

  componentDidMount() {
    fetch('/time').then(res => res.json()).then(data => {
      this.setState({currentTime: data.time});
    });
  }

    render(){
      return <Form action="/api/init_python" method="post" onSubmit={this.init_python}>
                <Form.Group>
                      <Form.Row>
                        <Form.Label column="lg" lg={2}>
                          Large Text
                        </Form.Label>
                        <Col xs="auto">
                          <Form.Control size="lg" type="text" placeholder="Large text" />
                        </Col>
                      </Form.Row>
                      <br />
                      <Form.Row>
                        <Form.Label column lg={2}>
                          Normal Text
                        </Form.Label>
                        <Col>
                          <Form.Control type="text" placeholder="Normal text" />
                        </Col>
                      </Form.Row>
                      <br />
                      <Form.Row>
                        <Form.Label column="sm" lg={2}>
                          Small Text
                        </Form.Label>
                        <Col>
                          <Form.Control size="sm" type="text" placeholder="Small text" />
                        </Col>
                      </Form.Row>
                </Form.Group>
                <Button variant="primary" type="submit">Valider les donn√©es</Button>
              </Form>
    }






    init_python(e) {
        e.preventDefault();
        const formData = new FormData();

        //Using this config in my request, the response gives me the mentioned waring of missing boundary
        const config = {
            headers:{
                'Content-Type' : 'multipart/form-data',
            }
        };

        formData.append("time", 0.2);

        axios.post("/api/init_python", formData, config)

    }










  }

  export default OpenSYR
;
