import '../App.css';
import React from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import Modal from 'react-bootstrap/Modal'
import axios from 'axios';

class Running extends React.Component{
  constructor(props) {
    super(props);          
    this.state={uploadProgress : 0, show : false, show2 : false,show3 : false};
    //ALLOW TO US KEY WORD "THIS" IN THOSE FUNCTIONS
    this.HandleSubmit = this.HandleSubmit.bind(this);  
    this.HandleSubmit2 = this.HandleSubmit2.bind(this); 
    this.HandleSubmit3 = this.HandleSubmit3.bind(this);
    this.HandleClose = this.HandleClose.bind(this);
    this.HandleClose2 = this.HandleClose2.bind(this);  
    this.HandleClose3 = this.HandleClose3.bind(this);  
    this.HandleClose4 = this.HandleClose4.bind(this);        
  }

  // EXECUTE REQUESTS ALL SECONDS TO GET INFORMATION
  componentDidMount() { 
    setInterval(() => {
      const config = {
        headers:{
            'Content-Type' : 'application/json',
        }
      };
      // REQUEST PERCENTAGE OF EXPERIENCE PROGRESS FOR THE PROGRESS BAR
      axios.post("/api/get_progress", 0,config)
      .then(response => {
        this.setState({ uploadProgress: parseInt(response.data.percentage) }) 
      })
      // REQUEST TO KONW IF THE SYRINGE IS SOON EMPTY
      axios.post("/api/syringe_empty_soon",0,config)
      .then(response=>{
        this.setState({ syringe_soon_empty: response.data.data }) 
      })
    }, 1000);                    
  }

  //STOP EXPERIENCE (stage 1)
  HandleSubmit(e){
    e.preventDefault();   
    this.setState({show3 : true});    
  }
  //STOP EXPERIENCE (stage 2-1)
  HandleClose3(e){
    e.preventDefault();
    this.setState({show3 : false});
    this.Stop_experience();
  }
  //STOP EXPERIENCE (stage 2-2)
  HandleClose4(e){
    e.preventDefault();
    this.setState({show3 : false});    
  }
  //STOP EXPERIENCE (stage 3)
  Stop_experience(){
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
  };
    axios.post("/api/stop_experience", 0,config)
    .then(response => {
        console.log(response)
    })
    window.location.href="/Home"
  }

  //END EXPERIENCE
  HandleSubmit2(e){
    e.preventDefault();
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
  };
    axios.post("/api/end_experience", 0,config)
    .then(response => {
        console.log(response)
    })
    window.location.href="/Home"
  }

  //CHANGE SYRINGE (stage 1)
  HandleSubmit3(e){
    e.preventDefault();     
    this.setState({show : true});           
  }

  //CHANGE SYRINGE (stage 2)
  HandleClose(e){
    e.preventDefault();
    this.setState({show : false});
    this.setState({show2 : true});
  }
  //CHANGE SYRINGE (stage 3)
  HandleClose2(e){
    e.preventDefault();    
    this.setState({show2 : false}); 
    this.Change_syringe();    
  }
  //CHANGE SYRINGE (stage 4)
  Change_syringe(){
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
    }; 
    axios.post("/api/change_syringe", 0,config)
    .then(response => {
        console.log(response)
    })
  }

    render(){
      return <div className="App-main" >        
                <br></br>                 
                <h1>The experience is running</h1><br></br><br></br><br></br>

                <ProgressBar variant="warning" now={this.state.uploadProgress} label={`${this.state.uploadProgress}% completed`} />

                <Form action="./api/stop_experience" method="post" onSubmit={this.HandleSubmit} >
                  <Button variant="danger" type="submit" disabled={this.state.uploadProgress > 100}>STOP EXPERIENCE</Button><br></br> 
                </Form>

                <Form action="./api/change_syringe" method="post" onSubmit={this.HandleSubmit3} >
                  <Button variant="primary" type="submit">Change syringe</Button>
                </Form> 

                <Form action="./api/end_experience" method="post" onSubmit={this.HandleSubmit2} >
                  <Button variant="primary" type="submit" disabled={this.state.uploadProgress < 100}>End experience</Button>
                </Form>


                <Modal show={this.state.show} >
                  <Modal.Header closeButton>
                    <Modal.Title>Syringe removing</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>Done removing the syringe ?</Modal.Body>
                  <Modal.Footer>
                    <Button id="1" variant="secondary" onClick={this.HandleClose}>
                      YES
                    </Button>                    
                  </Modal.Footer>
                </Modal>

                 <Modal show={this.state.show2}>
                  <Modal.Header closeButton>
                    <Modal.Title>Place new syringe</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>Done placing the syringe ?</Modal.Body>
                  <Modal.Footer>
                    <Button id="2" variant="secondary" onClick={this.HandleClose2}>
                      YES
                    </Button>                    
                  </Modal.Footer>
                </Modal> 

                <Modal show={this.state.show3}>
                  <Modal.Header closeButton>
                    <Modal.Title>Stop experience</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>Are you sure you want to stop the experience ?</Modal.Body>
                  <Modal.Footer>
                    <Button id="3" variant="secondary" onClick={this.HandleClose3}>
                      YES
                    </Button>
                    <Button id="4" variant="secondary" onClick={this.HandleClose4}>
                      NO
                    </Button>                    
                  </Modal.Footer>
                </Modal>              
            </div>
      }


}   
export default Running;
