import '../App.css';
import React from 'react';
import Button from 'react-bootstrap/Button'

import info from '../info.png';

class Initialisation extends React.Component{
  constructor(props) {
    super(props);   
  }

  HandleSubmit(e){
    e.preventDefault();
    //console.log("SUBMIT")
    const formData = new FormData();

    //Using this config in my request, the response gives me the mentioned waring of missing boundary
    const config = {
        headers:{
            'Content-Type' : 'application/json',
        }
    };

    formData.append("file", this.state.to_upload);

    axios.post("/api/init_python", this.state, config)
    .then(response => {
        console.log(response)
    })
  }
     render(){
      return  <div className="App-main" >
                <img src={info} className="Image-info"/>  
                <h1>Remove the syringe to start the initalisation</h1>
                <Button action="/api/initialisation" method="post" onSubmit={this.HandleSubmit} variant="primary" type="submit">Start initialisation</Button>  
              </div>
    }




  }

  export default Initialisation
;
