import '../App.css';
import React from 'react';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import axios from 'axios';

class OpenSYR extends React.Component{
  constructor(props) {
    super(props);
    this.state = { name: '' };
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value});
  }

  handleSubmit = (event) => {
    alert('A form was submitted: ' + this.state);

    fetch('https://your-node-server-here.com/api/endpoint', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(this.state)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
}

    render(){
      const data = {
        Diameter :this.state.diameter,
        Length :this.state.length,
        Volume :this.state.Volume
      };
      return  <div className="App-form" >
                <Form action="/api/init_python" method="post" onSubmit={this.init_python()}>
                  <Form.Group>
                        <Form.Row>
                          <Form.Label column="lg" lg={0}>
                            Syringe diameter
                          </Form.Label>
                              <Form.Control type="text" placeholder="Syringe diameter (cm)" value={this.state.diameter}
                              onChange={e => this.setState({ diameter: e.target.value })} />
                        </Form.Row>
                        <br />
                        <Form.Row>
                          <Form.Label column="lg" lg={0}>
                            Length of the experience
                          </Form.Label>
                            <Form.Control type="text" placeholder="Length of the experience (s)" value={this.state.length}
                            onChange={e => this.setState({ length: e.target.value })}/>
                        </Form.Row>
                        <br />
                        <Form.Row>
                          <Form.Label column="lg" lg={0}>
                            Volume to inject
                          </Form.Label>
                            <Form.Control type="text" placeholder="Volume to inject (mL)" value={this.state.volume}
                            onChange={e => this.setState({ volume: e.target.value })}/>
                        </Form.Row>
                    </Form.Group>
                  <Button variant="primary" type="submit">Launch experience</Button>                  
                </Form>
              </div>
    }


    init_python(e) {
        //e.preventDefault();
        let form = new FormData();
        form.append('Diameter',data.Diameter);
        form.append('Length',data.Length);
        form.append('Volume',data.Volume);
        let d = {
          method: 'post',
          url: "/api/init_python",
          data: form,
          config: {
            headers: {
              'Content-Type': 'multipart/form-data'
            },
          },
        };
        let req = axios(d);
        return new Promise((resolve)=>{
           req.then((res)=>{
               return resolve(res.data);
           });
        });
    }
}   
export default OpenSYR;
