import logo from '../logo.svg';
import '../App.css';
import React from 'react';
import {Container} from 'reactstrap';

import {Row, Col} from 'reactstrap';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'


class OpenSYR extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      currentTime: 0
    };
  }

  componentDidMount() {
    fetch('/time').then(res => res.json()).then(data => {
      this.setState({currentTime: data.time});
    });
  }

    render(){
      return <Form action="/api/init_python" method="post" onSubmit={this.init_python}>
                <Form.Group>
                    <Form.File
                    name="data"
                    type="file"
                    className="form-control-file"
                    id="inputGroupFile01"
                    label="TEST"
                    custom
                    />
                    <br/>
                    </Form.Group>
                    <Button variant="primary" type="submit">Execute Analysis</Button>
                </Form>
    }






    init_python(e) {
        e.preventDefault();
        const formData = new FormData();

        //Using this config in my request, the response gives me the mentioned waring of missing boundary
        const config = {
            headers:{
                'Content-Type' : 'multipart/form-data',
            }
        };

        formData.append("time", 0.2);

        axios.post("/api/init_python", formData, config)

    }










  }

  export default OpenSYR
;
