import '../App.css';
import React from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import Modal from 'react-bootstrap/Modal'
import axios from 'axios';

class Running extends React.Component{
  constructor(props) {
    super(props);          
    this.state={uploadProgress : 0, changeSyringe : false, stopExperience :false, show3 : false}
    this.HandleSubmit = this.HandleSubmit.bind(this);  
    this.HandleSubmit2 = this.HandleSubmit2.bind(this); 
    this.HandleSubmit3 = this.HandleSubmit3.bind(this);     
  }

  componentDidMount() {
    if(shouldComponentUpdate(nextProps,nextState)){      
      this.interval = setInterval(() => this.setState({ uploadProgress: this.state.uploadProgress+1 }), 1000);          
    }
  }

  shouldComponentUpdate(nextProps, nextState) {
    if ( this.state.uploadProgress >= 100 || this.state.changeSyringe || this.state.stopExperience) {      
      return false;
    }
    return true;
  }
  //STOP EXPERIENCE
  HandleSubmit(e){
    e.preventDefault();
    this.state.changeSyringe=true;
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
  };
    axios.post("/api/stop_experience", 0,config)
    .then(response => {
        console.log(response)
    })
  }
  //END EXPERIENCE
  HandleSubmit2(e){
    e.preventDefault();
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
  };
    axios.post("/api/end_experience", 0,config)
    .then(response => {
        console.log(response)
    })
    window.location.href="/Home"
  }
  //CHANGE SYRINGE
  HandleSubmit3(e){
    e.preventDefault();
    this.state.changeSyringe=true;
    this.state.show3 =true;   
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
  };    
    axios.post("/api/change_syringe", 0,config)
    .then(response => {
        console.log(response)
    })    
  }
    render(){
      return <div className="App-main" >        
                <br></br>                 
                <h1>The experience is running</h1><br></br><br></br><br></br>

                <ProgressBar variant="warning" animated now={this.state.uploadProgress} label={`${this.state.uploadProgress}% completed`} />
                <Form action="./api/stop_experience" method="post" onSubmit={this.HandleSubmit} >
                  <Button variant="danger" type="submit" disabled={this.state.uploadProgress > 100}>STOP EXPERIENCE</Button><br></br> 
                </Form>
                <Form action="./api/change_syringe" method="post" onSubmit={this.HandleSubmit3} >
                  <Button variant="primary" type="submit">Change syringe</Button>
                </Form> 
                <Form action="./api/end_experience" method="post" onSubmit={this.HandleSubmit2} >
                  <Button variant="primary" type="submit" disabled={this.state.uploadProgress < 100}>End experience</Button>
                </Form>


                <Modal show={this.state.show3}>
                  <Modal.Header closeButton>
                    <Modal.Title>Syringe removing</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>Done removing the syringe ?</Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={this.HandleClose3}>
                      YES
                    </Button>                    
                  </Modal.Footer>
                </Modal>              
            </div>
      }


}   
export default Running;
