import '../App.css';
import React from 'react';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import axios from 'axios';
import info from '../info.png';

class Initialisation extends React.Component{
  constructor(props) {
    super(props);
    this.state ={value : false, name : "Pause", temp : "Unpause",actual_name : 1};
    this.changeValue = this.changeValue.bind(this)
  }

  HandleSubmit(e){
    e.preventDefault();
    const config = {
      headers:{
          'Content-Type' : 'application/json',
      }
  };
    axios.post("/api/initialisation",0,config)
    window.location.href="/OpenSYR"   
  }

  changeValue(e){
    e.preventDefault();
    //pause
    if(this.state.actual_name===1){
        var tempo = this.state.name;
        this.setState({name : this.state.temp});
        this.setState({temp : tempo});
        this.setState({actual_name : 2});
    //unpause
    }else if(this.state.actual_name===2){
        tempo = this.state.temp;
        this.setState({temp :this.state.name});;
        this.setState({name : tempo});
        this.setState({actual_name : 1});
    }
  }

     render(){
      return  <div className="App-main" >
                <img src={info} className="Image-info" alt=""/> <br></br>
                <h1>Remove the syringes to start the initalisation</h1><br></br>
                <Form action="/api/initialisation" method="post" onSubmit={this.HandleSubmit}>
                <Button  variant="primary" onClick={e => this.setState({ value: true })} value={this.state.value}>Done</Button><br></br>  
                <Button  variant="primary" type="submit" disabled={!this.state.value}>Start initialisation</Button>
                </Form>
                <Button  variant="primary" type="submit" value={this.state.name} onClick={this.changeValue} ></Button>
              </div>
    }




  }

  export default Initialisation
;
